   private void FileButtonActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_FileButtonActionPerformed
   {//GEN-HEADEREND:event_FileButtonActionPerformed

	   JFileChooser browse = new JFileChooser();

	   browse.setDialogTitle("Browse");
	   browse.setFileSelectionMode(JFileChooser.DIRECTORIES_ONLY);
	   browse.setAcceptAllFileFilterUsed(false);
	   browse.setRequestFocusEnabled(rootPaneCheckingEnabled);
	   browse.setCurrentDirectory(new File("."));

	   if (browse.showOpenDialog(null) == JFileChooser.APPROVE_OPTION) {
		   if (browse.getSelectedFile().toString() == null) {
			   JOptionPane.showMessageDialog(null, "can't load the image", "Error", JOptionPane.ERROR_MESSAGE);
		   } else {
			   FilePath.setText(browse.getSelectedFile().toString());
		   }
	   }

   }//GEN-LAST:event_FileButtonActionPerformed

   private void jButton_RunActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_jButton_RunActionPerformed
   {//GEN-HEADEREND:event_jButton_RunActionPerformed

	   String Target = this.jTextField_Target.getText();
	   if (this.jCheckBox_isRegex.isSelected()) {
		   Target = Pattern.quote(Target);
	   }

	   String[][] cells = FileRenameing.Replace(this.FilePath.getText(), Target, this.jTextField_Replace.getText(), this.jCheckBox_isFiles.isSelected(), this.jCheckBox_isFolder.isSelected());
	   new GUItabelList(cells, this.colName, "Files").setVisible(true);

						   }//GEN-LAST:event_jButton_RunActionPerformed

