    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_jButton1ActionPerformed
    {//GEN-HEADEREND:event_jButton1ActionPerformed

		String Target = this.jTextField_Target.getText();
		if (this.jCheckBox_isRegex.isSelected()) {
			Target = Pattern.quote(Target);
		}

		String[][] cells = FileRenameing.Test(this.FilePath.getText(), Target, this.jTextField_Replace.getText(), this.jCheckBox_isFiles.isSelected(), this.jCheckBox_isFolder.isSelected());
		new GUItabelList(cells, this.colName, "Files").setVisible(true);

    }//GEN-LAST:event_jButton1ActionPerformed

